
public class Array_In_Reverse_order {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int arr [] = {2,5,7};
		for (int iterator = 0; iterator < arr.length; iterator++ ) {		
		}
		for (int i = arr.length-1; i >= 0; i--) {  
            System.out.print(arr[i] + " ");  
        }  
	}

}
 